import React, { Component } from 'react';
var array=[]
 class Form extends Component {
    documentData;
    constructor(props) {
        super(props);



        this.state = {
            title: '',
            id: '',
            body: ''
        }
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    // on form submit...
    handleFormSubmit() {

        array.push(this.state)
        console.log(array)
        localStorage.setItem('document', JSON.stringify(array));
        this.setState({
            id:'',
            title:'',
            body:''
        })
    }


    render() {
        return (
            <div className="container">
                <form >
                    <div className="form-group">
                        <label>Title</label>
                        <input type="text" name="title" className="form-control" value={this.state.title} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label>id</label>
                        <input type="number" name="id" className="form-control" value={this.state.id} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label>Body</label>
                        <input type="text" name="body" className="form-control" value={this.state.body} onChange={this.handleChange} />
                    </div>
                    <button onClick={()=>{this.handleFormSubmit()}} className="btn btn-primary btn-block" >Submit</button>
                </form>
            </div>
        ) 
    }
}
export default Form
