//import data from './post.json';
import React, { useState } from 'react';

function Form() {
    const [name, setName] = useState({ Id: "", UserId: "", Title: "", Body: "" })
    function show() {
        return (
            <>
                <h4>Id:{name.Id}</h4>
                <h4>user Id:{name.UserId}</h4>
                <h4>Title:{name.Title}</h4>
                <h4>Body:{name.Body}</h4>
            </>
        )
    }

    return (
        <>
            <form>
                <input type='number'
                    placeholder='enter id'
                    value={name.Id}
                    onChange={e => setName({ ...name, Id: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter user-id'
                    value={name.UserId}
                    onChange={e => setName({ ...name, UserId: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter title'
                    value={name.Title}
                    onChange={e => setName({ ...name, Title: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter body'
                    value={name.value}
                    onChange={e => setName({ ...name, Body: e.target.value })}
                /><br />
                <a href='/' onClick={show}>submit</a>
                <p>{JSON.stingify(name)}</p>
            </form>


        </>
    )
}
export default Form;
