//import data from './post.json';
import React, { useState } from 'react';

function Form() {
    const [name, setName] = useState({ Id: "", UserId: "", Title: "", Body: "" })
    const handleSubmit = event => {
        event.preventDefault();
        alert(`Your state values: \n 
                email: ${Title} \n 
                You can replace this alert with your process`);
      };

    return (
        <>

            <form onSubmit={handleSubmit}> 
                <input type='number'
                    placeholder='enter id'
                    value={name.Id}
                    onChange={e => setName({ ...name, Id: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter user-id'
                    value={name.UserId}
                    onChange={e => setName({ ...name, UserId: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter title'
                    value={name.Title}
                    onChange={e => setName({ ...name, Title: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter body'
                    value={name.value}
                    onChange={e => setName({ ...name, Body: e.target.value })}
                /><br />
                <a type='submit' >submit</a>
                <p>{JSON.stringify(name)}</p>
            </form>


        </>
    )

}
export default Form;
