//import data from './post.json';
import React,{useState} from 'react';

function Form(){
    const [name ,setName]= useState({Id:"",UserId:"",Title:"",Body:""})

    return (
        <>
        <form>
        <input type='number'
         placeholder='enter id'
         value={name.Id}
         onChange={e=>setName({Id:e.target.value})}
         /><br/>
        <input type='text'
         placeholder='enter user-id'
         value={name.UserId}
         onChange={e=>setName({UserId:e.target.value})}
         / ><br/>
        <input type='text'
         placeholder='enter title'
         value={name.Title}
         onChange={e=>setName({Title:e.target.value})}
         /><br/>
        <input type='text'
         placeholder='enter body'
         value={name.value}
         onChange={e=>setName({Body:e.target.value})}
         / ><br/>
       <a href='/'>submit</a>
       </form>
       <h4>{name.Id}</h4>
       <h4>{name.UserId}</h4>
       <h4>{name.Title}</h4>
       <h4>{name.Body}</h4>
       </>    
)
}
export default Form;
