import data from './a.json';
import {Route,Switch,Link}  from 'react-router-dom';
import B from './b'
import Form from './Form'
var element=data;
class All extends React.Component{
    constructor(){
        super()
        this.state={
            arr:[]
        }
    }
    componentDidMount()
    {
        element.map((e)=>
            (
                this.setState({arr:e})

        ))


    }

    render()
    {
        return(



           element.map((e,i)=>
            (

                    <Switch>

                        <Route  exact path='/' >
                    <li><Link to={'/b/'+e.id} >{e.title}</Link></li></Route>
                    <Route  exact path='/b/:id' component={B}><B /></Route>
                    <Route exact path='/form' component={Form}></Route>
                    </Switch>

            ),
            <a href='/form'>create</a>,

            )

            )

    }
}
export default All;
