//import data from './post.json';
import React, { useState ,useEffect} from 'react';

function Form() {
    const [name, setName] = useState({ Id: "", UserId: "", Title: "", Body: "" })
    const handleSubmit = event => {
        event.preventDefault();

        //var alldata=JSON.stringify(name)
        //document.write(alldata)
        localStorage.setItem('document',JSON.stringify(name));
      };
      useEffect(() => {
        documentData = JSON.parse(localStorage.getItem('document'));
        if (localStorage.getItem('document')) {
            useState({
                title: documentData.Title,
               id: documentData.id,
               UserId: documentData.UserId,
               Body:documentData.Body
        })
    } else {
        this.setState({
            Title: '',
            Id: '',
            UserId: '',
            Body:''
        })}
      });

    return (
        <>

            <form onSubmit={handleSubmit}> 
                <input type='number'
                    placeholder='enter id'
                    value={name.Id}
                    onChange={e => setName({ ...name, Id: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter user-id'
                    value={name.UserId}
                    onChange={e => setName({ ...name, UserId: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter title'
                    value={name.Title}
                    onChange={e => setName({ ...name, Title: e.target.value })}
                /><br />
                <input type='text'
                    placeholder='enter body'
                    value={name.value}
                    onChange={e => setName({ ...name, Body: e.target.value })}
                /><br />
                <button type="submit">
                     Submit
                </button>

            </form>


        </>
    )

}
export default Form;
