import React, { Component } from 'react';

 class Form extends Component {
    documentData;
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        array=[]
        this.state = {
            title: '',
            id: '',
            body: ''
        }
    }
    show (){
        array.push(this.state)
        localStorage.setItem('document',JSON.stringify(array));
         this.setState({
             id:'',
             title:'',
             body:''
         })
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    // on form submit...
    handleFormSubmit(e) {
        e.preventDefault()
        localStorage.setItem('document', JSON.stringify(this.state));
    }

    // React Life Cycle
    componentDidMount() {
        this.documentData = JSON.parse(localStorage.getItem('document'));

        if (localStorage.getItem('document')) {
            this.setState({
                title: this.documentData.title,
                id: this.documentData.id,
                body: this.documentData.body
            })
        } else {
            this.setState({
                title: '',
                id: '',
                body: ''
            })
        }
    }

    render() {
        return (
            <div className="container">
                <form onSubmit={this.handleFormSubmit}>
                    <div className="form-group">
                        <label>Title</label>
                        <input type="text" name="title" className="form-control" value={this.state.title} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label>id</label>
                        <input type="number" name="id" className="form-control" value={this.state.id} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label>Body</label>
                        <input type="text" name="body" className="form-control" value={this.state.body} onChange={this.handleChange} />
                    </div>
                    <button type="submit" className="btn btn-primary btn-block" onClick={()=>{this.show()}}>Submit</button>
                </form>
            </div>
        )
    }
}
export default Form
